import React from 'react';
import { Map, TileLayer, Marker, Popup } from "react-leaflet";
import { Icon } from "leaflet";
import "./mapsequences.css";

// import axios, { get } from 'axios';

export default class MapSequences extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			sequences: [
				{
					"id": 24,
					"gb_id": "DQ362940.1",
					"sequence": "",
					"latitude": 30.0,
					"longitude": 95.0,
					"fasta": 36
			},
			{
					"id": 25,
					"gb_id": "DQ362939.1",
					"sequence": "",
					"latitude": 15.0,
					"longitude": 125.0,
					"fasta": 36
			},
			{
					"id": 26,
					"gb_id": "AY530092.1",
					"sequence": "",
					"latitude": 45.0,
					"longitude": 75.0,
					"fasta": 36
			},
			{
					"id": 26,
					"gb_id": "GARCA.1",
					"sequence": "GARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCAGARCA",
					//"latitude": -38.416097,
					//"longitude": -63.616672,
					"latitude": -63.616672,
					"longitude": -38.416097,
					"fasta": 36
					// si pongo los valores invertidos funciona, sino termina en el agua...
			}
    ],
			activeSeq: null,
		};
	}

	setActiveSeq = (seq) => {
		this.setState(
			{activeSeq:seq}
		)
	}

	render() {
		const seqs = this.state.sequences;
		const activeSeq = this.state.activeSeq;
		return (
			<div className='container'>
				<Map center={[45.4, -75.7]} zoom={2}>
					<TileLayer
					url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
					attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
					noWrap
					/>
					{
						seqs.map( seq => (
							<Marker
								key={seq.id}
								position={[
									seq.longitude,
									seq.latitude,
								]}
								attribution={seq.id}
								onclick ={ () => {this.setActiveSeq(seq);}}
							/>
						))
					}
					{activeSeq && (
						<Popup
						position={[
							activeSeq.latitude,
							activeSeq.longitude,
						]}
						onClose={() => {
							this.setActiveSeq(null);
						}}
					>
						<div>
							<h2>{activeSeq.gb_id}</h2>
						</div>
					</Popup>
					)}
				</Map>
			</div>
		);
	}
}